mapper.update:
UPDATE MK_USER_DETAILS 
SET USER_STATUS = #{user_status}
WHERE USER_ID=#{user_id}

UPDATE MK_TERMS_AGREEMENT
SET TERMS_ID = #{terms_id}, TERMS_CONTENT = #{terms_content}, TERMS_AGREE_DATE_MODIFIED = NOW()
WHERE TERMS_SERIAL = #{terms_serial};

UPDATE MK_NOTICE SET 
NOTICE_TITLE = #{notice_title}, NOTICE_CONTENT = #{notice_content}
WHERE NOTICE_SERIAL = #{notice_serial}

UPDATE MK_FAQ SET 
FAQ_TITLE = #{faq_title}, FAQ_CONTENT = #{faq_content}, FAQ_CATEGORY = #{faq_category} 
WHERE FAQ_SERIAL = #{faq_serial}

UPDATE MK_QNA_PERSONAL SET 
QNA_PERSONAL_ANSWER = #{qna_personal_answer}
WHERE QNA_PERSONAL_SERIAL = #{qna_personal_serial}

